pragma solidity 0.6.12;

import "./elliptic-curve-solidity/examples/Secp256k1.sol";

contract Swap {
	Secp256k1 secp256k1;

	// the hash where the pre-image must be disclosed to redeem the eth
	// in this contract.
	// it is the keccak256 hash of the redeeming party's secret (s_b), which when disclosed
	// allows the contract creator to redeem funds on another chain.
	bytes32 public hashRedeem;

	// the hash where the pre-image must also be disclosed to redeem the eth in this
	// contract. it is the keccak256 hash of a secret known initially by the owner,
	// which is disclosed to the other party when the owner sees the funds locked
	// on the other chain.
	// this prevents the case where the owner locks their funds in this contract, 
	// and the other party doesn't lock theirs, but still redeems the funds here.
	bytes32 public hashOwnerSecret;

	// the hash of the expected public key for which the secret s_b corresponds
	// this public key lies on the secp256k1 curve
	bytes32 public expectedPublicKey;

	// the hash where the pre-image must be disclosed by the contract owner after time
	// t to refund the eth in this contract.
	// it is the keccak256 hash of a contract owner's secret, which when disclosed 
	// allows the other party to refund their coins on the other chain.
	bytes32 public hashRefund;

	// time after which a refund is allowed
	uint256 timeout;

	// contract creator
	address payable owner;

	constructor(
		bytes32 _hashRedeem,
		bytes32 _hashOwnerSecret, 
		bytes32 _expectedPublicKey, 
		bytes32 _hashRefund
	) public payable {
		owner = msg.sender;
		hashRedeem = _hashRedeem;
		hashOwnerSecret = _hashOwnerSecret;
		expectedPublicKey = _expectedPublicKey;
		hashRefund = _hashRefund;
		timeout = now + 1 days; // TODO: make configurable
		secp256k1 = new Secp256k1();
	}

	function redeem(
		uint256 _sOther,
		uint256 _sOwner
	) public {
		// confirm that provided secret `_sOther` is pre-image of `hashRedeem`
		bytes32 h0 = keccak256(abi.encode(_sOther));
		require(h0 == hashRedeem);

		// confirm that provided secret `_sOwner` is pre-image of `hashOwnerSecret`
		bytes32 h1 = keccak256(abi.encode(_sOwner));
		require(h1 == hashOwnerSecret);

		// confirm that secret corresponds to provided public key
		(uint256 px, uint256 py) = secp256k1.derivePubKey(_sOther);
		bytes32 ph = keccak256(abi.encode(px, py));
		require(ph == expectedPublicKey);

		// send eth to caller
		msg.sender.transfer(address(this).balance);
	}

	function refund(
		uint256 _s
	) public {
		// confirm that provided secret is pre-image of `hashRefund`
		bytes32 h = keccak256(abi.encode(_s));
		require(h == hashRefund);

		// send eth back to owner
		owner.transfer(address(this).balance);
	}
}
